const gridSize = 20;

class GameMap {
    constructor(height, width, initialPacmanX, initialPacmanY, powerupSpawns, map) {
        this.height = height;
        this.width = width;
        this.initialPacmanX = initialPacmanX*gridSize;
        this.initialPacmanY = initialPacmanY*gridSize;
        this.powerupSpawns = powerupSpawns;
        this.map = map;
    }
}

