const gridSize = 20;

class GameMap {
    constructor(height, width, initialPacmanX, initialPacmanY, map) {
        this.height = height;
        this.width = width;
        this.initialPacmanX = initialPacmanX*gridSize;
        this.initialPacmanY = initialPacmanY*gridSize;
        this.map = map;
    }
}

